mapper.select:
  select goods_no, goods_name, price, discount, discount_rate, hit, image_name, saved_rate
  	from(
  		Select rownum rnum, goods_no, goods_name, price, discount, discount_rate, hit, image_name, saved_rate
	  	from(
		  Select 
		  		g.goods_no, g.goods_name, p.price, p.discount, p.discount_rate, g.hit, g.image_name, p.saved_rate
		  from 
		  		goods g, price p
		  	where
		  		(


					sale_startDate <= sysdate
					and sysdate <= sale_endDate



				)
				and
				(
					g.goods_no = p.goods_no
				)

		  	order by g.goods_no desc
	  	)
	)
	where rnum between #{startRow} and #{endRow}

mapper.insert:
insert into goods_image(image_no, goods_no, image_name) select goods_image_seq.nextval, goods_no, image_name from(
select #{vo.goods_no} goods_no, #{vo.image_name} image_name from dual
)

insert into goods_sizeColor(goods_sizeColor_no, goods_no, size_no, color_no) select goods_image_seq.nextval, goods_no, size_no, color_no from(
select #{vo.goods_no} goods_no, #{vo.size_no} size_no, #{vo.color_no} color_no from dual
)

insert into goods_option(goods_option_no, goods_no, option_name) select goods_image_seq.nextval, goods_no, option_name from(
select #{vo.goods_no} goods_no, #{vo.option_name} option_name from dual
)

mapper.select:
select size_no, cate_code1, cate_code2, size_Name
from basic_size
where cate_code1 = #{cate_code1}

select color_no, cate_code1, cate_code2, color_Name
from basic_color
where cate_code1 = #{cate_code1}
